package Controlstatemts;

public class Assignment6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
		int odd=0;
       for(int i=0;i<=10;i++)
       {
    	   if(i%2==0)
    	   {
    		   
    		   sum=sum+i;
    	   }
    	   else
    	   {
    		   odd=odd+i;
    	   }
       }
       System.out.println(sum);
       System.out.println(odd);
	}
	

}
